mapper.select:
			SELECT COUNT(*) AS CNT
            FROM T4_BOARD
            WHERE BOARD_KIND = 'T_LAN';

			SELECT COUNT(*) AS CNT
            FROM T4_BOARD
            WHERE BOARD_KIND = 'D_LAN';

mapper.update:
UPDATE T4_BOARD 
SET HITS = HITS + 1
WHERE BOARD_NO = #{boardNo}

UPDATE T4_BOARD 
SET FINISH_YN = 'Y'
WHERE BOARD_NO = #{boardNo}

UPDATE T4_BOARD
SET DELETE_YN = 'Y'
WHERE BOARD_NO = #{boardNo}

mapper.delete:
UPDATE T4_COMMENT
SET DELETE_YN = 'Y'
WHERE COMMENT_NO = #{commentNo} OR COMMENT_RENO = #{commentNo}

UPDATE T4_COMMENT
SET DELETE_YN = 'Y'
WHERE COMMENT_NO = #{commentNo}

mapper.select:
SELECT A.FUNDING_NO, FUNDING_NAME, FUNDING_SUMMARY, FUNDING_SCRIPT, FUNDING_START_DT, FUNDING_END_DT, FUNDING_CLOSE_YN, FUNDING_GOAL_CNT, FUNDING_PRICE, FORMAT(FUNDING_PRICE , 0) AS FUNDING_PRICE2, CDATETIME, UDATETIME, CNT, TIMESTAMPDIFF(DAY, NOW(), FUNDING_END_DT) AS D_DAY, CONCAT(IMG_PATH, C.IMG_NAME) AS IMG_PATH, DATE_FORMAT(FUNDING_END_DT,'%k') AS END_TIME
FROM T4_FUNDING A LEFT JOIN (SELECT FUNDING_NO, count(FUNDING_NO) as CNT FROM T4_FUNDING_MEMBER GROUP BY FUNDING_NO) B ON A.FUNDING_NO = B.FUNDING_NO LEFT JOIN (SELECT FUNDING_NO, IMG_PATH, IMG_NAME FROM T4_F_IMAGE WHERE THUMBNAIL_YN = 'Y' GROUP BY FUNDING_NO) C ON A.FUNDING_NO = C.FUNDING_NO
WHERE A.FUNDING_NO = #{fundingNo}

SELECT COUNT(*) AS CNT
FROM T4_FUNDING_MEMBER
WHERE FUNDING_NO = #{fundingNo} AND USER_ID = #{sessionId}
