mapper.select:
			SELECT COUNT(*) AS CNT
            FROM T4_CARDINFO
            WHERE DELETE_YN = 'N';

mapper.update:
UPDATE T4_CARDINFO
SET DELETE_YN = 'Y'
WHERE CARD_NO = #{cardNo}

UPDATE T4_BOARD
SET HITS = HITS + 1
WHERE BOARD_NO = #{boardNo}

UPDATE T4_BOARD
SET DELETE_YN = 'Y'
WHERE BOARD_NO = #{boardNo}

UPDATE T4_COMMENT
SET DELETE_YN = 'Y'
WHERE COMMENT_NO = #{commentNo}
