mapper.update:
UPDATE T4_RECIPE
SET DELETE_YN = 'Y'
WHERE RECIPE_NO = #{recipeNo}

mapper.select:
SELECT COUNT(*) AS CNT
FROM T4_USER_RECIPE
WHERE RECIPE_NO = #{recipeNo};

SELECT P.RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, IMG_PATH, CODE AS CODE, HASHTAG FROM ( SELECT R.RECIPE_NO AS RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, HASHTAG, DELETE_YN, CDATETIME, UDATETIME, IMG_PATH FROM T4_RECIPE R INNER JOIN (SELECT RECIPE_NO, IMG_PATH FROM T4_R_IMAGE WHERE THUMBNAIL_YN = 'Y') I ON I.RECIPE_NO = R.RECIPE_NO ) R LEFT JOIN T4_R_PURPOSE P ON P.RECIPE_NO = R.RECIPE_NO WHERE DELETE_YN = 'N'
AND CODE = #{recipe_code}
LIMIT #{startNum}, 12

SELECT H.RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, IMG_PATH, H.CODE AS HCODE, HASHTAG FROM ( SELECT R.RECIPE_NO AS RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, HASHTAG, DELETE_YN, CDATETIME, UDATETIME, IMG_PATH FROM T4_RECIPE R INNER JOIN (SELECT RECIPE_NO, IMG_PATH FROM T4_R_IMAGE WHERE THUMBNAIL_YN = 'Y') I ON I.RECIPE_NO = R.RECIPE_NO ) R LEFT JOIN T4_HOWTO H ON H.RECIPE_NO = R.RECIPE_NO WHERE DELETE_YN = 'N'
AND H.CODE = #{recipe_code}
LIMIT #{startNum}, 12

SELECT T.RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, IMG_PATH, T.CODE AS TCODE, HASHTAG FROM ( SELECT R.RECIPE_NO AS RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, HASHTAG, DELETE_YN, CDATETIME, UDATETIME, IMG_PATH FROM T4_RECIPE R INNER JOIN (SELECT RECIPE_NO, IMG_PATH FROM T4_R_IMAGE WHERE THUMBNAIL_YN = 'Y') I ON I.RECIPE_NO = R.RECIPE_NO ) R LEFT JOIN T4_TOOL T ON T.RECIPE_NO = R.RECIPE_NO WHERE DELETE_YN = 'N'
AND T.CODE = #{recipe_code}
LIMIT #{startNum}, 12

SELECT R.RECIPE_NO AS RECIPE_NO, RECIPE_NAME, TIME, DIFFICULTY, RECIPE_HITS, HASHTAG, DELETE_YN, CDATETIME, UDATETIME, COOK_INGRE, IMG_PATH, T.CODE AS TCODE, TNAME FROM T4_RECIPE R INNER JOIN (SELECT RECIPE_NO, IMG_PATH FROM T4_R_IMAGE WHERE THUMBNAIL_YN = 'Y') I ON I.RECIPE_NO = R.RECIPE_NO LEFT JOIN ( SELECT RECIPE_NO, T.CODE, C.NAME AS TNAME FROM T4_TOOL T LEFT JOIN T4_CODE C ON C.CODE = T.CODE WHERE C.KIND = 'TOOL') T ON T.RECIPE_NO = R.RECIPE_NO
WHERE R.RECIPE_NO = #{recipeNo}
ORDER BY RAND() LIMIT 1

SELECT COUNT(*) AS CNT
FROM T4_RECIPE
WHERE RECIPE_NO = #{recipeNo}
